package com.mrothberg.kakumei.app;

import android.annotation.SuppressLint;
import android.app.Application;
import android.content.Context;

import org.solovyev.android.checkout.Billing;

import com.mrothberg.kakumei.database.DatabaseManager;
import com.mrothberg.kakumei.managers.PrefManager;

public class App extends Application {

    private static App instance;

    private final Billing billing = new Billing(this, new Billing.DefaultConfiguration() {
        @Override
        public String getPublicKey() {
            return "";
        }
    });

    @SuppressLint("StaticFieldLeak")
    private static Context context;

    public App() {
        instance = this;
    }

    @Override
    public void onCreate() {
        context = getApplicationContext();
        DatabaseManager.init(getApplicationContext());
        PrefManager.init(getApplicationContext());
        super.onCreate();
    }

    public static App get() {
        return instance;
    }

    public Billing getBilling() {
        return billing;
    }

    public static Context getContext() {
        return context;
    }
}
